const raw = $input.first().json.text || $input.first().json.content || '';
let br;
try {
  const cleaned = raw.replace(/```json\n?/g, '').replace(/```\n?/g, '').trim();
  br = JSON.parse(cleaned);
} catch (e) {
  throw new Error('Agent 2 parse error: ' + e.message);
}
const sd = br.score_breakdown;
const fit_score = Math.round((sd.must_haves*0.40)+(sd.experience*0.25)+(sd.adjacency*0.20)+(sd.culture*0.15));
let status, proceed_to_scheduling, routing_key;
if      (fit_score >= 85) { status='Qualified/High';   proceed_to_scheduling=true;  routing_key='qualified'; }
else if (fit_score >= 70) { status='Qualified/Medium'; proceed_to_scheduling=true;  routing_key='qualified'; }
else if (fit_score >= 60) { status='Borderline';       proceed_to_scheduling=false; routing_key='borderline'; }
else                      { status='Rejected';          proceed_to_scheduling=false; routing_key='rejected'; }
const a1 = $('Parse Agent 1 Output').first().json;
return [{ json: {
  candidate: a1.candidate,
  job_requirements: a1.job_requirements,
  job_id: a1.job_id,
  fit_score, status, confidence: br.confidence,
  score_breakdown: sd,
  strengths: br.strengths,
  gaps: br.gaps,
  proceed_to_scheduling,
  routing_key,
  candidate_name:  a1.candidate?.name  || '',
  candidate_email: a1.candidate?.email || '',
  job_title:       a1.job_requirements?.job_title || ''
} }];

const SLOT_MS=45*60*1000,BUFFER_MS=15*60*1000,MAX=5;
const busy=$input.all().filter(i=>i.json.start?.dateTime).map(i=>({s:new Date(i.json.start.dateTime).getTime(),e:new Date(i.json.end.dateTime).getTime()}));
const PREFS=[9*60,9*60+30,10*60,10*60+30,11*60,11*60+30,13*60,13*60+30,14*60,14*60+30,15*60];
function free(s,e){return!busy.some(b=>s<b.e+BUFFER_MS&&e>b.s-BUFFER_MS);}
const slots=[];
const now=Date.now();
const d=new Date(now+24*60*60*1000);
d.setHours(0,0,0,0);
while(d.getTime()<now+14*24*60*60*1000&&slots.length<MAX){
  if(d.getDay()!==0&&d.getDay()!==6){
    for(const m of PREFS){
      if(slots.length>=MAX)break;
      const ss=new Date(d);ss.setHours(Math.floor(m/60),m%60,0,0);
      const se=new Date(ss.getTime()+SLOT_MS);
      if(ss.getTime()>now&&free(ss.getTime(),se.getTime())){
        slots.push({start:ss.toISOString(),end:se.toISOString(),display:ss.toLocaleString('en-US',{weekday:'long',month:'long',day:'numeric',hour:'numeric',minute:'2-digit',timeZone:'America/New_York'})+' ET'});
      }
    }
  }
  d.setDate(d.getDate()+1);
}
if(!slots.length)throw new Error('No free slots in next 14 days');
const sc=$('Calculate Score').first().json;
return[{json:{...sc,available_slots:slots}}];

